<#.SYNOPSIS    Clears all obj and bin folders in the current directory and subdirectories..DESCRIPTION    This script recursively searches for and removes all 'obj' and 'bin' folders     in the current directory structure. Useful for cleaning up build artifacts     in .NET projects..EXAMPLE    .\Clear-BuildFolders.ps1    .NOTES    Author: Nguyen    Date: 2026-02-09#># Get the current directory$currentPath = Join-Path (Get-Location) "src"Write-Host "Starting cleanup process in: $currentPath" -ForegroundColor CyanWrite-Host "Searching for 'obj' and 'bin' folders..." -ForegroundColor Yellow# Find all obj and bin folders$foldersToDelete = Get-ChildItem -Path $currentPath -Include "obj", "bin" -Recurse -Directory -ErrorAction SilentlyContinueif ($foldersToDelete.Count -eq 0) {    Write-Host "No 'obj' or 'bin' folders found." -ForegroundColor Green    exit}Write-Host "Found $($foldersToDelete.Count) folder(s) to delete:" -ForegroundColor Yellow$foldersToDelete | ForEach-Object { Write-Host "  - $($_.FullName)" -ForegroundColor Gray }# Confirm deletion$confirmation = Read-Host "`nDo you want to delete these folders? (Y/N)"if ($confirmation -eq 'Y' -or $confirmation -eq 'y') {    $deletedCount = 0    $failedCount = 0        foreach ($folder in $foldersToDelete) {        try {            Remove-Item -Path $folder.FullName -Recurse -Force -ErrorAction Stop            Write-Host "Deleted: $($folder.FullName)" -ForegroundColor Green            $deletedCount++        }        catch {            Write-Host "Failed to delete: $($folder.FullName) - $($_.Exception.Message)" -ForegroundColor Red            $failedCount++        }    }        Write-Host "`nCleanup completed!" -ForegroundColor Cyan    Write-Host "Successfully deleted: $deletedCount folder(s)" -ForegroundColor Green        if ($failedCount -gt 0) {        Write-Host "Failed to delete: $failedCount folder(s)" -ForegroundColor Red    }}else {    Write-Host "Operation cancelled by user." -ForegroundColor Yellow}